{{{1}}} <small><nowiki>[</nowiki>[[Old Style and New Style dates|O.S.]] {{{3}}}<nowiki>]</nowiki></small> {{{2|}}}<noinclude> {{Pp-template|small=yes}}
[[Category:Time, date and calendar templates|{{PAGENAME}}]]
[[af:Sjabloon:OuVormDatum]] [[et:Mall:VanaKalendriAeg]] [[lv:Veidne:Dat VS]] [[mk:Шаблон:Датум по стар стил]] [[ml:ഫലകം:OldStyleDate]] [[ja:Template:OldStyleDate]] [[no:Mal:OldStyleDate]] [[ru:Шаблон:OldStyleDate]] [[tl:Suleras:OldStyleDate]] [[vi:Tiêu bản:OldStyleDate]] [[zh:Template:OldStyleDate]] </noinclude>

{{#switch:none
 |{{#iferror: {{#time:Y_M_d|{{{1|}}} }} | none }} <!-- #time: can't handle -->
 |{{#iferror: {{#expr: {{{1|}}}+0 }}
    |<!--not a pure number-->
    |{{#ifexpr: {{{1|}}}+0 > 10000000000000
       |<!-- a yyyymmddhhmmss timestamp -->
       |{{#ifeq: {{#expr:{{{1|}}}+0}} | {{{1|}}}
          | none <!-- pure number eg 123.456 -->
          | <!-- assume yy-mm-dd -->
        }}
     }}
  }}
 |{{#switch:  {{lc:{{{2|}}}}} | none | asis=none }}
 |{{#ifexpr:  {{#time:Y|{{{1|}}} }} < 1000 | none }}
 |{{#switch:  {{#time:Ynj|{{{1|}}} }}|100031|110031|130031|140031|150031=none}}
 |= {{{1|}}}<!-- error or "none", so no formatting -->
 |<!-- continue with formatting -->
  {{#ifeq:<!--
    -->{{#time:Y|{{{1}}} 2008}}<!--
    -->{{#iferror: {{#ifexpr: {{{1}}}>10000000000000 | no }} | }}<!--
    -->{{#time:Y|{{{1}}} 2004}}
  |20082004
  |<!-- no year -->
    {{#ifeq:{{#time:d|{{{1}}} 2036}}|{{#time:d|{{{1}}} }}
    |<!-- month+day -->{{#time:
      {{#switch: {{lc: {{#ifeq:{{{3|}}}|y|L}}{{{2|}}} }}
      | lmdy | liso | lymd      = [[:F j]]
      | mdy  | iso  | ymd       = F j
      | ldmy | l                = [[:j F]]
      | #default                = j F
      }}|{{{1}}} 2000 }}<!-- default='dmy' or null or "" or unsupported option -->
    |<!-- month only -->{{#time:
      {{#switch: {{lc: {{#ifeq:{{{3|}}}|y|L}}{{{2|}}} }}
      | lmdy | liso | lymd 
      | ldmy | l                = [[F]]
      | #default                = F
      }}|{{{1}}} 2000 }}<!-- default='dmy'/'mdy'/'ymd'/'iso'/null/""/unsupported opt -->
    }}
  |<!-- with year-->
    {{#if: {{#iferror:{{#time:j|2 {{{1|}}}}}|*D*|{{#iferror:{{#time:j|2000 {{{1|}}}}}|*D*| }}}}
    |<!-- day+month+year -->{{#time:
      {{#switch: {{lc: {{#ifeq:{{{3|}}}|y|L}}{{{2|}}} }}
      | lmdy                    = [[:F j]], [[Y]]
      | mdy                     = F j, Y
      | liso                    = [[Y|Y-]][[F j|m-d]]<!-- i.e. [[Y-m-d]] -->
      | iso                     = Y-m-d
      | lymd                    = [[Y]] [[:F j]]
      | ymd                     = Y F j
      | ldmy | l                = [[:j F]] [[Y]]
      | #default                = j F Y
      }}|{{{1|}}} }}<!-- #default='dmy' or null or "" or unsupported option -->
    |<!-- month+year -->{{#time:
      {{#switch: {{lc: {{#ifeq:{{{3|}}}|y|L}}{{{2|}}} }}
      | lmdy | liso | lymd | ldmy | l  = [[:F Y]]
      | #default                = F Y
      }}|{{{1|}}} }}<!-- default='dmy'/'iso'/'mdy'/null/""/unsupported option -->
    }}
  }}    
